class ThreeArray {    public static void main (String[] args) {        int [][][] stock = {            {{50, 30, 30}, {40, 25 , 15}},            {{60, 35, 25}, {55, 28, 18}}        };        int totalWarehouse2 = 0;                for(int[][] warehouse : stock){            for(int[] section : warehouse){                for(int item : section){                    System.out.println(item);                }            }        }        for (int[] section: stock[1]){            for(int item : section){                totalWarehouse2 += item;            }        }        System.out.println("Total stock in Warehouse 2 = " + totalWarehouse2);}}
